mapper.update:

update announcement_type
set announcement_type_is_deleted   = #{announcementTypeIsDeleted,jdbcType=BOOLEAN},
    announcement_type_is_forbidden = #{announcementTypeIsForbidden,jdbcType=BOOLEAN},
    announcement_type_name         = #{announcementTypeName,jdbcType=VARCHAR}
where announcement_type_id = #{announcementTypeId,jdbcType=VARCHAR}

mapper.select:
select *
from announcement_type
             left join announcement a
        on announcement_type.announcement_type_id = a.announcement_announcement_type_id


        and announcement_type_id = #{param4.announcementTypeId}


        and announcement_type_name like CONCAT('%', #{param4.announcementTypeName}, '%')


        and announcement_type_is_deleted = #{param4.announcementTypeIsDeleted}


        and announcement_type_is_forbidden = #{param4.announcementTypeIsForbidden}


order by #{param3}
offset #{param1} limit #{param2}
